import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  Box,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  TextField,
  Typography,
  Alert,
} from "@mui/material";

const CostTable = () => {
  const [costs, setCosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [filteredCosts, setFilteredCosts] = useState([]);
  const [providerFilter, setProviderFilter] = useState("");
  const [teamFilter, setTeamFilter] = useState("");
  const [budget, setBudget] = useState("");
  const [totalCost, setTotalCost] = useState(0);
  const [isOverBudget, setIsOverBudget] = useState(false);

  // Fetch data from backend
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:3001/api/costs");
        setCosts(response.data);
        setFilteredCosts(response.data);
        calculateTotalCost(response.data);
        setLoading(false);
      } catch (err) {
        console.error("❌ Error fetching data:", err);
        setError("Failed to fetch data.");
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // Calculate total cost
  const calculateTotalCost = (data) => {
    const total = data.reduce((sum, item) => sum + parseFloat(item.cost), 0);
    setTotalCost(total);
    setIsOverBudget(budget && total > parseFloat(budget));
  };

  // Handle filtering
  const handleFilter = () => {
    let filteredData = [...costs];

    if (providerFilter) {
      filteredData = filteredData.filter((cost) =>
        cost.provider_name?.toLowerCase().includes(providerFilter.toLowerCase())
      );
    }

    if (teamFilter) {
      filteredData = filteredData.filter((cost) =>
        cost.team_name?.toLowerCase().includes(teamFilter.toLowerCase())
      );
    }

    setFilteredCosts(filteredData);
    calculateTotalCost(filteredData);
  };

  if (loading) return <Typography>Loading data...</Typography>;
  if (error) return <Typography color="error">{error}</Typography>;

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" gutterBottom>
        Billing Costs Table
      </Typography>

      {/* 📝 Budget Input */}
      <Box sx={{ display: "flex", gap: 2, alignItems: "center", mb: 3 }}>
        <TextField
          label="Enter your budget"
          type="number"
          value={budget}
          onChange={(e) => {
            setBudget(e.target.value);
            calculateTotalCost(filteredCosts);
          }}
          variant="outlined"
          size="small"
        />
        <Typography variant="body1">
          Total Cost: <strong>${totalCost.toFixed(2)}</strong>
        </Typography>
      </Box>

      {isOverBudget && (
        <Alert severity="warning" sx={{ mb: 2 }}>
          ⚠️ Warning: Budget Exceeded!
        </Alert>
      )}

      {/* 🔎 Filters */}
      <Box sx={{ display: "flex", gap: 2, mb: 3 }}>
        <TextField
          label="Filter by Provider"
          value={providerFilter}
          onChange={(e) => setProviderFilter(e.target.value)}
          variant="outlined"
          size="small"
        />
        <TextField
          label="Filter by Team"
          value={teamFilter}
          onChange={(e) => setTeamFilter(e.target.value)}
          variant="outlined"
          size="small"
        />
        <Button variant="contained" onClick={handleFilter}>
          Apply Filters
        </Button>
        <Button
          variant="outlined"
          onClick={() => {
            setFilteredCosts(costs);
            calculateTotalCost(costs);
          }}
        >
          Reset Filters
        </Button>
      </Box>

      {/* 📊 Data Table */}
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Service Name</TableCell>
              <TableCell>Project Name</TableCell>
              <TableCell>Team Name</TableCell>
              <TableCell>Cost</TableCell>
              <TableCell>Date</TableCell>
              <TableCell>Provider Name</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {filteredCosts.map((cost, index) => (
              <TableRow key={index}>
                <TableCell>{cost.service_name}</TableCell>
                <TableCell>{cost.project_name}</TableCell>
                <TableCell>{cost.team_name}</TableCell>
                <TableCell>${parseFloat(cost.cost).toFixed(2)}</TableCell>
                <TableCell>
                  {new Date(cost.date).toLocaleDateString()}
                </TableCell>
                <TableCell>{cost.provider_name}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Box>
  );
};

export default CostTable;
