import React, { useEffect, useState } from "react";
import axios from "axios";
import "./CostTable.css"; // במידה ויש עיצוב מותאם אישית

const CostTable = () => {
  const [costs, setCosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [filteredCosts, setFilteredCosts] = useState([]);
  const [providerFilter, setProviderFilter] = useState("");
  const [teamFilter, setTeamFilter] = useState("");
  const [sortBy, setSortBy] = useState("");

  // Fetch data from backend
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:3001/api/costs");
        setCosts(response.data);
        setFilteredCosts(response.data);
        setLoading(false);
      } catch (err) {
        console.error("❌ Error fetching data:", err);
        setError("Failed to fetch data.");
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // Handle filtering
  const handleFilter = () => {
    let filteredData = [...costs];

    if (providerFilter) {
      filteredData = filteredData.filter(
        (cost) =>
          cost.provider_name &&
          cost.provider_name
            .toLowerCase()
            .includes(providerFilter.toLowerCase())
      );
    }

    if (teamFilter) {
      filteredData = filteredData.filter(
        (cost) =>
          cost.team_name &&
          cost.team_name.toLowerCase().includes(teamFilter.toLowerCase())
      );
    }

    setFilteredCosts(filteredData);
  };

  // Handle sorting
  const handleSort = (key) => {
    const sortedData = [...filteredCosts].sort((a, b) => {
      if (key === "cost") {
        return parseFloat(a[key]) - parseFloat(b[key]);
      }
      if (key === "date") {
        return new Date(a[key]) - new Date(b[key]);
      }
      return a[key]?.localeCompare(b[key]);
    });
    setFilteredCosts(sortedData);
    setSortBy(key);
  };

  if (loading) return <p>Loading data...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="cost-table-container">
      <h2>Billing Costs Table</h2>

      {/* Filter Controls */}
      <div className="filters">
        <input
          type="text"
          placeholder="Filter by Provider"
          value={providerFilter}
          onChange={(e) => setProviderFilter(e.target.value)}
        />
        <input
          type="text"
          placeholder="Filter by Team"
          value={teamFilter}
          onChange={(e) => setTeamFilter(e.target.value)}
        />
        <button onClick={handleFilter}>Apply Filters</button>
        <button onClick={() => setFilteredCosts(costs)}>Reset Filters</button>
      </div>

      {/* Sort Buttons */}
      <div className="sort-buttons">
        <button onClick={() => handleSort("service_name")}>
          Sort by Service
        </button>
        <button onClick={() => handleSort("project_name")}>
          Sort by Project
        </button>
        <button onClick={() => handleSort("team_name")}>Sort by Team</button>
        <button onClick={() => handleSort("cost")}>Sort by Cost</button>
        <button onClick={() => handleSort("date")}>Sort by Date</button>
      </div>

      {/* Data Table */}
      <table className="cost-table">
        <thead>
          <tr>
            <th>Service Name</th>
            <th>Project Name</th>
            <th>Team Name</th>
            <th>Cost</th>
            <th>Date</th>
            <th>Provider Name</th>
          </tr>
        </thead>
        <tbody>
          {filteredCosts.map((cost, index) => (
            <tr key={index}>
              <td>{cost.service_name}</td>
              <td>{cost.project_name}</td>
              <td>{cost.team_name}</td>
              <td>${parseFloat(cost.cost).toFixed(2)}</td>
              <td>{new Date(cost.date).toLocaleDateString()}</td>
              <td>{cost.provider_name}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default CostTable;
