import React, { useState, useEffect } from "react";
import axios from "axios";

const CostTable = () => {
  const [data, setData] = useState([]);
  const [sortField, setSortField] = useState("cost");
  const [sortOrder, setSortOrder] = useState("asc");
  const [provider, setProvider] = useState("");
  const [team, setTeam] = useState("");

  useEffect(() => {
    fetchData();
  }, [sortField, sortOrder, provider, team]);

  const fetchData = async () => {
    try {
      let query = `http://localhost:3001/api/costs?`;
      if (provider) query += `provider=${provider}&`;
      if (team) query += `team=${team}&`;
      if (sortField) query += `sort=${sortField}&order=${sortOrder}`;

      console.log(`🔄 Fetching data from: ${query}`);
      const response = await axios.get(query);
      console.log("✅ Data fetched:", response.data);

      setData(response.data);
    } catch (error) {
      console.error("❌ Error fetching mock data:", error);
    }
  };

  const handleSort = (field) => {
    const order = sortField === field && sortOrder === "asc" ? "desc" : "asc";
    setSortField(field);
    setSortOrder(order);
  };

  return (
    <div>
      <h2>Cost Table</h2>
      <table>
        <thead>
          <tr>
            <th onClick={() => handleSort("service_name")}>Service Name</th>
            <th onClick={() => handleSort("project_name")}>Project Name</th>
            <th onClick={() => handleSort("team_name")}>Team Name</th>
            <th onClick={() => handleSort("cost")}>Cost</th>
            <th onClick={() => handleSort("date")}>Date</th>
            <th onClick={() => handleSort("provider_name")}>Provider</th>
          </tr>
        </thead>
        <tbody>
          {data.length > 0 ? (
            data.map((row, index) => (
              <tr key={index}>
                <td>{row.service_name}</td>
                <td>{row.project_name}</td>
                <td>{row.team_name}</td>
                <td>{row.cost}</td>
                <td>{new Date(row.date).toLocaleDateString()}</td>
                <td>{row.provider_name}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="6">No data available</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default CostTable;
