import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Select,
  MenuItem,
  InputLabel,
  FormControl,
  Button,
  TextField,
  Alert,
  Typography,
} from "@mui/material";

const CostTable = () => {
  const [data, setData] = useState([]);
  const [provider, setProvider] = useState("");
  const [team, setTeam] = useState("");
  const [sortField, setSortField] = useState("");
  const [sortOrder, setSortOrder] = useState("asc");
  const [budget, setBudget] = useState(3000);
  const [totalCost, setTotalCost] = useState(0);
  const [overBudget, setOverBudget] = useState(false);
  const [providerSummary, setProviderSummary] = useState({});

  // Fetch data on load or when filters change
  useEffect(() => {
    fetchData();
  }, [provider, team, sortField, sortOrder]);

  // Calculate total cost and provider summary
  useEffect(() => {
    const total = data.reduce((sum, item) => sum + parseFloat(item.cost), 0);
    setTotalCost(total);
    setOverBudget(total > budget);

    const summary = data.reduce((acc, item) => {
      acc[item.provider_name] =
        (acc[item.provider_name] || 0) + parseFloat(item.cost);
      return acc;
    }, {});
    setProviderSummary(summary);
  }, [data, budget]);

  // Fetch data from server
  const fetchData = async () => {
    try {
      let query = `http://localhost:3001/api/costs?`;
      if (provider) query += `provider=${provider}&`;
      if (team) query += `team=${team}&`;
      if (sortField) query += `sort=${sortField}&order=${sortOrder}`;

      const response = await axios.get(query);
      setData(response.data);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  // Handle sorting
  const handleSort = (field) => {
    if (sortField === field) {
      setSortOrder(sortOrder === "asc" ? "desc" : "asc");
    } else {
      setSortField(field);
      setSortOrder("asc");
    }
  };

  // Refresh data
  const handleRefresh = () => {
    fetchData();
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>FinOps Dashboard</h2>

      {/* Filter by Provider */}
      <FormControl style={{ marginRight: "10px", minWidth: 120 }}>
        <InputLabel>Provider</InputLabel>
        <Select value={provider} onChange={(e) => setProvider(e.target.value)}>
          <MenuItem value="">All</MenuItem>
          <MenuItem value="AWS">AWS</MenuItem>
          <MenuItem value="Google Cloud">GCP</MenuItem>
        </Select>
      </FormControl>

      {/* Filter by Team */}
      <FormControl style={{ marginRight: "10px", minWidth: 120 }}>
        <InputLabel>Team</InputLabel>
        <Select value={team} onChange={(e) => setTeam(e.target.value)}>
          <MenuItem value="">All</MenuItem>
          <MenuItem value="Team A">Team A</MenuItem>
          <MenuItem value="Team B">Team B</MenuItem>
          <MenuItem value="Team C">Team C</MenuItem>
        </Select>
      </FormControl>

      {/* Budget Input */}
      <TextField
        label="Budget"
        type="number"
        value={budget}
        onChange={(e) => setBudget(parseFloat(e.target.value))}
        style={{ marginRight: "10px", width: "120px" }}
      />

      {/* Sorting Buttons */}
      <Button variant="contained" onClick={() => handleSort("cost")}>
        Sort by Cost{" "}
        {sortField === "cost" ? (sortOrder === "asc" ? "⬆️" : "⬇️") : ""}
      </Button>

      <Button variant="contained" onClick={() => handleSort("date")}>
        Sort by Date{" "}
        {sortField === "date" ? (sortOrder === "asc" ? "⬆️" : "⬇️") : ""}
      </Button>

      {/* Refresh Button */}
      <Button
        variant="outlined"
        onClick={handleRefresh}
        style={{ marginLeft: "10px" }}
      >
        Refresh 🔄
      </Button>

      {/* Over Budget Alert */}
      {overBudget && (
        <Alert severity="error" style={{ marginTop: "20px" }}>
          ⚠️ Total cost (${totalCost.toFixed(2)}) exceeds the budget (${budget}
          )!
        </Alert>
      )}

      {/* Provider Cost Summary */}
      <div style={{ marginTop: "20px" }}>
        <Typography variant="h6">Total Cost by Provider:</Typography>
        {Object.entries(providerSummary).map(([providerName, total]) => (
          <Typography key={providerName}>
            {providerName}: ${total.toFixed(2)}
          </Typography>
        ))}
      </div>

      {/* Data Table */}
      <TableContainer component={Paper} style={{ marginTop: "20px" }}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>
                <strong>Provider Name</strong>
              </TableCell>
              <TableCell>
                <strong>Service Name</strong>
              </TableCell>
              <TableCell>
                <strong>Project Name</strong>
              </TableCell>
              <TableCell>
                <strong>Team Name</strong>
              </TableCell>
              <TableCell>
                <strong>Service Cost</strong>
              </TableCell>
              <TableCell>
                <strong>Date</strong>
              </TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {data.length > 0 ? (
              data.map((row, index) => (
                <TableRow key={index}>
                  <TableCell>{row.provider_name}</TableCell>
                  <TableCell>{row.service_name}</TableCell>
                  <TableCell>{row.project_name}</TableCell>
                  <TableCell>{row.team_name}</TableCell>
                  <TableCell>${parseFloat(row.cost).toFixed(2)}</TableCell>
                  <TableCell>
                    {new Date(row.date).toLocaleDateString()}
                  </TableCell>
                </TableRow>
              ))
            ) : (
              <TableRow>
                <TableCell colSpan={6} align="center">
                  No data available
                </TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default CostTable;
